mapper.select:
SELECT fs.nick_name AS fensName, fml.fens_id AS fensId, fml.star_id AS starId, IFNULL(SUM(fs.vigour_val),0) AS totalVigourVal FROM fens_mark_log fml LEFT JOIN fens fs ON fs.id = fml.fens_id
and fml.add_time >= #{startTime,jdbcType=VARCHAR}
and fml.add_time <= #{endTime,jdbcType=VARCHAR}
GROUP BY fml.fens_id ORDER BY
totalVigourVal asc
totalVigourVal desc
LIMIT ${(pageNum-1) * pageSize}, ${pageSize}

SELECT
COUNT(DISTINCT fml.fens_id ) AS totalCount
FROM fens_mark_log fml
LEFT JOIN fens fs ON fs.id = fml.fens_id


        and	fml.add_time >=  #{startTime,jdbcType=VARCHAR}


        and	fml.add_time <=  #{endTime,jdbcType=VARCHAR}
