mapper.select:
SELECT st.name AS starName, st.id AS starId, st.avatar AS starAvatar, IFNULL(SUM(hl.vigour_val),0) AS totalVigourVal FROM hit_list hl LEFT JOIN star st ON hl.star_id = st.id
and hl.create_time >= #{startTime,jdbcType=VARCHAR}
and hl.create_time <= #{endTime,jdbcType=VARCHAR}
GROUP BY hl.star_id ORDER BY
totalVigourVal asc
totalVigourVal desc
LIMIT ${(pageNum-1) * pageSize}, ${pageSize}

SELECT
COUNT(DISTINCT hl.star_id ) AS totalCount
FROM hit_list hl
LEFT JOIN star st ON hl.star_id = st.id


        and	hl.create_time >=  #{startTime,jdbcType=VARCHAR}


        and	hl.create_time <=  #{endTime,jdbcType=VARCHAR}

SELECT fs.nick_name AS fensName, hl.fens_id AS fensId, hl.star_id AS starId, IFNULL(SUM(hl.vigour_val),0) AS totalVigourVal FROM hit_list hl LEFT JOIN fens fs ON fs.id = hl.fens_id
and hl.create_time >= #{startTime,jdbcType=VARCHAR}
and hl.create_time <= #{endTime,jdbcType=VARCHAR}
and hl.star_id = #{starId}
GROUP BY hl.fens_id ORDER BY
totalVigourVal asc
totalVigourVal desc
LIMIT ${(pageNum-1) * pageSize}, ${pageSize}

SELECT
COUNT(DISTINCT hl.fens_id) AS totalCount
FROM hit_list hl
LEFT JOIN fens fs ON fs.id = hl.fens_id


        and	hl.create_time >=  #{startTime,jdbcType=VARCHAR}


        and	hl.create_time <=  #{endTime,jdbcType=VARCHAR}

SELECT t.rank_no FROM (
    SELECT
        @rownum:=@rownum + 1 AS rank_no,
        star_id,
        sum( vigour_val ) as val
        FROM
        ( SELECT @rownum := 0 ) r,
        hit_list
        WHERE
        create_time >=   #{startTime,jdbcType=VARCHAR}
        AND create_time <=   #{endTime,jdbcType=VARCHAR}
        GROUP BY
        star_id  order by val desc
) t where star_id = #{id}
