mapper.select:
SELECT st.name AS starName, st.id AS starId, IFNULL(SUM(hl.vigour_val),0) AS totalVigourVal FROM hit_list hl LEFT JOIN star st ON hl.star_id = st.id
and hl.create_time >= #{startTime,jdbcType=VARCHAR}
and hl.create_time <= #{endTime,jdbcType=VARCHAR}
GROUP BY hl.star_id ORDER BY
totalVigourVal asc
totalVigourVal desc
LIMIT ${(pageNum-1) * pageSize}, ${pageSize}

SELECT
COUNT(DISTINCT hl.star_id ) AS totalCount
FROM hit_list hl
LEFT JOIN star st ON hl.star_id = st.id


        and	hl.create_time >=  #{startTime,jdbcType=VARCHAR}


        and	hl.create_time <=  #{endTime,jdbcType=VARCHAR}
